// src/components/FundList.js
import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import * as XLSX from "xlsx";
import "../styles/styles.css";

const FundList = () => {
  const { t } = useTranslation();
  const [funds, setFunds] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    // Load the Excel file and parse it
    fetch(require("./DataSource.xlsx"))
      .then((response) => response.arrayBuffer())
      .then((data) => {
        const workbook = XLSX.read(data, { type: "array" });
        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
        setFunds(jsonData);
      })
      .catch((error) => console.error("Error loading the Excel file:", error));
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredFunds = funds.filter(
    (fund) =>
      fund.基金代码?.includes(searchTerm) || fund.基金名称?.includes(searchTerm)
  );

  return (
    <div className="fund-list-container">
      <h1>{t("基金列表")}</h1>
      <input
        type="text"
        placeholder={t("搜索基金")}
        value={searchTerm}
        onChange={handleSearch}
        className="search-input"
      />
      <table className="fund-list-table">
        <thead>
          <tr>
            <th>{t("基金代码")}</th>
            <th>{t("基金名称")}</th>
          </tr>
        </thead>
        <tbody>
          {filteredFunds.map((fund, index) => (
            <tr key={index}>
              <td>{fund.基金代码}</td>
              <td>{fund.基金名称}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FundList;
