// src/components/Navbar.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import "../styles/styles.css";
import LanguageSwitcher from "./LanguageSwitcher"; // Ensure this import is correct

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const { t, i18n } = useTranslation(); // Hook to use translations
  const navigate = useNavigate();

  // Set default language to Chinese
  React.useEffect(() => {
    i18n.changeLanguage("zh");
  }, [i18n]);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    navigate("/login"); // Redirect to the login page after logging out
  };

  return (
    <nav className="navbar">
      <ul className="navbar-links">
        <li>
          <Link to="/">{t("home")}</Link> {/* Translated Home */}
        </li>
        <li>
          <Link to="/market">{t("市场")}</Link> {/* Translated Market */}
        </li>
        <li>
          <Link to="/funds">{t("基金")}</Link> {/* Translated Funds */}
        </li>
        <li>
          <Link to="/selection">{t("延选")}</Link> {/* Translated Selection */}
        </li>
        {!isLoggedIn ? (
          <>
            <li>
              <Link to="/register">{t("注册")}</Link>{" "}
              {/* Translated Register */}
            </li>
            <li>
              <Link to="/login">{t("登录")}</Link> {/* Translated Login */}
            </li>
          </>
        ) : (
          <>
            <li>
              <button onClick={handleLogout} className="logout-button">
                {t("登出")} {/* Translated Logout */}
              </button>
            </li>
          </>
        )}
        {/* Add the Language Switcher here */}
        <li>
          <LanguageSwitcher />
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
