// src/i18n.js
import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
  en: {
    translation: {
      // Add your English translations here
      welcome_message: "Welcome to the site!",
      one_stop_solution: "Your one-stop solution.",
      register_now: "Register Now",
      login: "Log In",
      fund_list: "Fund List",
      create_fund: "Create Fund",
      view_funds: "View Funds",
      update_fund: "Update Fund",
      save_fund: "Save Fund",
      fund_name: "Fund Name",
      net_value: "Net Value",
      cumulative_net_value: "Cumulative Net Value",
    },
  },
  zh: {
    translation: {
      // Add your Chinese translations here
      welcome_message: "欢迎来到网站!",
      one_stop_solution: "您的一站式解决方案。",
      register_now: "立即注册",
      login: "登录",
      fund_list: "基金列表",
      create_fund: "创建基金",
      view_funds: "查看基金",
      update_fund: "更新基金",
      save_fund: "保存基金",
      fund_name: "基金名称",
      net_value: "净值",
      cumulative_net_value: "累计净值",
    },
  },
  // Add more languages here...
};

i18n.use(initReactI18next).init({
  resources,
  lng: "en", // default language
  interpolation: {
    escapeValue: false, // React already does escaping
  },
});

export default i18n;
