import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import "../styles/styles.css";

const FundList = () => {
  const [funds, setFunds] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/DataSource.xlsx");
        const arrayBuffer = await response.arrayBuffer();
        const data = new Uint8Array(arrayBuffer);
        const workbook = XLSX.read(data, { type: "array" });

        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
        console.log("Funds loaded:", jsonData); // Check the data structure here
        setFunds(jsonData);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, []);

  // Displaying the first item in the console to verify the structure
  if (funds.length > 0) {
    console.log("First fund item structure:", funds[0]);
  }

  const filteredFunds = funds.filter((fund) => {
    const code = fund.code ? fund.code.toString() : ""; // Convert to string
    const fundname = fund.fundname ? fund.fundname.toString() : ""; // Convert to string
    return code.includes(searchTerm) || fundname.includes(searchTerm);
  });

  return (
    <div className="fund-list-container">
      <h1>基金列表</h1>
      <input
        type="text"
        placeholder="搜索基金"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <table>
        <thead>
          <tr>
            <th>基金代码</th>
            <th>基金名称</th>
            {/* Add more columns if needed */}
          </tr>
        </thead>
        <tbody>
          {filteredFunds.length > 0 ? (
            filteredFunds.map((fund, index) => (
              <tr key={index}>
                <td>{fund.code}</td>
                <td>{fund.fundname}</td>
                {/* Add more columns here to display additional data */}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="2">无匹配结果</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default FundList;
