// src/components/SingleFund.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Line } from "react-chartjs-2";
import Chart from "chart.js/auto";

const SingleFund = () => {
  const { fundname } = useParams();
  const [fundDetails, setFundDetails] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("/fundDetails.json")
      .then((response) => response.json())
      .then((data) => {
        setFundDetails(data[fundname] || []);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching fund details:", error);
        setLoading(false);
      });
  }, [fundname]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (fundDetails.length === 0) {
    return <p>No details available for this fund.</p>;
  }

  const dates = fundDetails.map((detail) => detail.date);
  const totalNav = fundDetails.map((detail) => detail.totalnav);
  const unitNav = fundDetails.map((detail) => detail.unitnav);

  const data = {
    labels: dates,
    datasets: [
      {
        label: "Total NAV",
        data: totalNav,
        borderColor: "rgba(75,192,192,1)",
        backgroundColor: "rgba(75,192,192,0.2)",
        fill: true,
      },
      {
        label: "Unit NAV",
        data: unitNav,
        borderColor: "rgba(153,102,255,1)",
        backgroundColor: "rgba(153,102,255,0.2)",
        fill: true,
      },
    ],
  };

  return (
    <div>
      <h1>{fundname}</h1>
      <Line data={data} />
    </div>
  );
};

export default SingleFund;
