// src/components/Navbar.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import "../styles/styles.css";
import LanguageSwitcher from "./LanguageSwitcher"; // Ensure this import is correct

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const { t, i18n } = useTranslation(); // Hook to use translations

  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    navigate("/login"); // Redirect to the login page after logging out
  };

  const changeLanguage = (lang) => {
    if (i18n && typeof i18n.changeLanguage === "function") {
      i18n.changeLanguage(lang); // Safely handle language change
    } else {
      console.error("changeLanguage function is not available on i18n.");
    }
  };

  return (
    <nav className="navbar">
      <ul className="navbar-links">
        <li>
          <Link to="/">{t("home")}</Link> {/* Translated Home */}
        </li>
        <li>
          <Link to="/funds">{t("fund_list")}</Link> {/* Translated Fund List */}
        </li>
        <li>
          <Link to="/create-fund">{t("create_fund")}</Link>{" "}
          {/* Translated Create Fund */}
        </li>
        {/* Add other links here */}
        {!isLoggedIn ? (
          <>
            <li>
              <Link to="/register">{t("register_now")}</Link>{" "}
              {/* Translated Register */}
            </li>
            <li>
              <Link to="/login">{t("login")}</Link> {/* Translated Login */}
            </li>
          </>
        ) : (
          <>
            <li>
              <button onClick={handleLogout} className="logout-button">
                {t("logout")} {/* Translated Logout */}
              </button>
            </li>
          </>
        )}
        {/* Language Switcher */}
        <li>
          <select
            onChange={(e) => changeLanguage(e.target.value)}
            className="language-switcher"
            defaultValue={i18n.language}
          >
            <option value="en">English</option>
            <option value="zh">中文</option>
          </select>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
