// src/components/Login.js

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next"; // Import useTranslation hook
import "../styles/styles.css";

const Login = ({ setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const { t } = useTranslation(); // Initialize translation hook

  const handleLogin = async () => {
    try {
      const response = await fetch(
        "http://localhost:5001/api/auth/login", // Ensure the correct backend endpoint
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ email, password }),
        }
      );

      const data = await response.json();

      if (response.ok) {
        // Save login state or token if needed
        setIsLoggedIn(true);
        localStorage.setItem("isLoggedIn", "true");

        // Redirect to the homepage
        navigate("/");
      } else {
        setError(data.message || "Login failed");
      }
    } catch (err) {
      setError("Network error");
    }
  };

  return (
    <div className="login-container">
      <h1>{t("login")}</h1> {/* Translated Login */}
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder={t("email")} // Translated Email
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder={t("password")} // Translated Password
      />
      <button onClick={handleLogin}>{t("login")}</button>{" "}
      {/* Translated Login Button */}
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
