import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import "../styles/styles.css";

const FundList = () => {
  const [funds, setFunds] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/DataSource.xlsx"); // Ensure this path is correct
        const arrayBuffer = await response.arrayBuffer();
        const data = new Uint8Array(arrayBuffer);
        const workbook = XLSX.read(data, { type: "array" });

        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
        setFunds(jsonData);
        console.log("Funds loaded:", jsonData); // Debugging line to check if data is loaded
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, []);

  const filteredFunds = funds.filter(
    (fund) =>
      fund["基金代码"].toLowerCase().includes(searchTerm.toLowerCase()) ||
      fund["基金名称"].toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="fund-list-container">
      <h1>基金列表</h1>
      <input
        type="text"
        placeholder="搜索基金"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <table>
        <thead>
          <tr>
            <th>基金代码</th>
            <th>基金名称</th>
          </tr>
        </thead>
        <tbody>
          {filteredFunds.length > 0 ? (
            filteredFunds.map((fund, index) => (
              <tr key={index}>
                <td>{fund["基金代码"]}</td>
                <td>{fund["基金名称"]}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="2">无匹配结果</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default FundList;
