import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";

const FundList = () => {
  const [funds, setFunds] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("/DataSource.xlsx");
        const arrayBuffer = await response.arrayBuffer();
        const data = new Uint8Array(arrayBuffer);
        const workbook = XLSX.read(data, { type: "array" });

        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
        const jsonData = XLSX.utils.sheet_to_json(firstSheet);
        setFunds(jsonData);
      } catch (error) {
        console.error("Error loading data:", error);
      }
    };

    fetchData();
  }, []);

  // Convert Excel serial date to JavaScript date
  const convertExcelDateToJSDate = (serial) => {
    const excelEpoch = new Date(1899, 11, 30);
    const date = new Date(excelEpoch.setDate(excelEpoch.getDate() + serial));
    return date.toISOString().split("T")[0];
  };

  const filteredFunds = funds.filter((fund) => {
    const code = fund["code"] ? fund["code"].toString().toLowerCase() : "";
    const fundname = fund["fundname"]
      ? fund["fundname"].toString().toLowerCase()
      : "";
    const lowerCaseSearchTerm = searchTerm.toLowerCase();
    return (
      code.includes(lowerCaseSearchTerm) ||
      fundname.includes(lowerCaseSearchTerm)
    );
  });

  return (
    <div className="fund-list-container">
      <h1>基金列表</h1>
      <input
        type="text"
        placeholder="搜索基金"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <table>
        <thead>
          <tr>
            <th>基金代码</th>
            <th>基金名称</th>
            <th>总净值</th>
            <th>单位净值</th>
            <th>日期</th>
          </tr>
        </thead>
        <tbody>
          {filteredFunds.length > 0 ? (
            filteredFunds.map((fund, index) => (
              <tr key={index}>
                <td>{fund["code"]}</td>
                <td>{fund["fundname"]}</td>
                <td>{fund["totalnav"]}</td>
                <td>{fund["unitnav"]}</td>
                <td>{convertExcelDateToJSDate(fund["date"])}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">无匹配结果</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default FundList;
